var dpMap map[int]int

func stoneGameVII(stones []int) int {
	n := len(stones)
	sums := make([]int,n+1)
	sums[0] = 0
	for i := 1 ; i <= n; i++ {
		sums[i] = sums[i-1] + stones[i-1]
	}
	fmt.Println(n,sums)
	start := 0
	end := n
	dpMap = make(map[int]int)
	return dp1(start,end,sums)

}
func dp1(start int,end int,sums []int) int{
	if start >= end {
		return 0
	}
	key := start*1000 + end
	if val,ok := dpMap[key]; ok {
		return val
	}
	result1 := sums[end] -sums[start+1]
	result2 := sums[end-1] -sums[start]
	//return max(result1 - dp2(start+1,end,sums),result2 - dp2(start,end-1,sums))
	maxResult := max(result1 - dp1(start+1,end,sums),result2 - dp1(start,end-1,sums))
	dpMap[key] = maxResult
	return maxResult
}
func max(a int,b int) int {
	if a > b {
		return a
	}
	return b
}

func dp2(start int,end int,sums []int) int{
	if start >= end {
		return 0
	}
	result1 := sums[end] -sums[start+1]
	result2 := sums[end-1] -sums[start]
	return max(result1 - dp1(start +1,end,sums),result2 - dp1(start,end-1,sums))
}
func min(a int,b int) int {
	if a < b {
		return a
	}
	return b
}
